import { Text, TouchableOpacity, StyleSheet, Image } from "react-native";
import React, { useState, useEffect } from "react";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { purchasedProduct } from "../src/Products";



export default function Button(props) {
  return (
    <TouchableOpacity
      style={styles.button}
      onPress={() => {
        if (!props.disabled) {
          props.navigation.navigate(props.screen);
        } else {
          props.onPress(); // Appel de la fonction onPress pour mettre à jour l'état du produit
        }
      }}
    >
      <Image style={styles.image} source={props.image} />
      <Text style={styles.text}>{props.text} </Text>
      {props.isSelected && <Text>OK</Text>}
      {props.price && <Text>{props.price}€</Text>}
    </TouchableOpacity>
  );
}

// Styles restent inchangés

const styles = StyleSheet.create({
  button: {
    flex: 1,
    alignItems: "center",
    backgroundColor: "rgba(45, 45, 45, 0.3)",
    flexDirection: "row",
    height: 100,
    padding: "3%",
    borderWidth: 1,
    margin: "1%",
  },
  text: {
    color: "white",
    fontSize: 15,
    marginLeft: 10,
  },
  image: {
    width: 60,
    height: 60,
  },
});
