import { Text, TouchableOpacity, StyleSheet, Image, View } from "react-native";
import React from "react";
export default function Button(props: any) {
  return (
    <TouchableOpacity
      style={styles.button}
      onPress={() => {
        if (!props.disabled) {
          props.navigation.navigate(props.screen);
        }
      }}
    >
      <Image style={styles.image} source={props.image} />
      <Text style={styles.text}>{props.text}</Text>
      <View style={styles.textContainer}>
        {props.price && <Text style={styles.price}>{props.price}â‚¬</Text>}
      </View>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  button: {
    flex: 1,
    alignItems: "center",
    backgroundColor: "rgba(45, 45, 45, 0.3)",
    flexDirection: "row",
    height: 100,
    padding: "3%",
    borderWidth: 1,
    margin: "1%",
  },
  textContainer: {
    flex: 1,
    flexDirection: "row",
    marginLeft: 10,
  },
  text: {
    color: "white",
    fontSize: 15,
  },
  image: {
    width: 60,
    height: 60,
  },
  price: {
    alignSelf: "flex-end",
    color: "white",
  },
});
