import React, { useState } from "react";
import { View, Text, ImageBackground } from "react-native";
import styles from "../../services/styles";
import Button from "../../components/Button";
import { purchasedProduct } from "../Products";

const poissons = [
  { title: "Filet Bar de ligne ", price: 7, purchase: false },
  { title: "Bar de ligne portion ", price: 10, purchase: false },
  { title: "Aile de raie ", price: 10, purchase: false },
  { title: "Lieu jaune de ligne ", price: 12, purchase: false },
  { title: "Filet Julienne ", price: 19, purchase: false },
  { title: "Bar de ligne ", price: 30, purchase: false },
];

const logo = require("../../assets/images/logo/poulpe.png");
const disabled = true;

const ListPoissons = () => {
  const handleProductPress = (product) => {
    product.purchase = !product.purchase;

    if (product.purchase) {
      purchasedProduct.push(product);
    } else {
      const index = purchasedProduct.findIndex((item) => item === product);
      if (index !== -1) {
        purchasedProduct.splice(index, 1);
      }
    }
    console.log("Produits sélectionnés : ", purchasedProduct);
  };

  return (
    <View style={styles.container}>
      <ImageBackground
        source={require("../../assets/images/background.png")}
        style={styles.imagebackground}
      >
        {poissons.map((poisson, index) => (
          <Button
            key={index}
            disabled={disabled}
            text={poisson.title}
            image={logo}
            price={poisson.price}
            onPress={() => handleProductPress(poisson)}
            isSelected={poisson.purchase}
          />
        ))}
      </ImageBackground>
    </View>
  );
};

export default ListPoissons;
