import { Text, TouchableOpacity, StyleSheet, Image } from "react-native";
import React, { useState, useEffect } from "react";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { purchasedProduct } from "../src/Products";


export default function Button(props: any) {
  const [isSelected, setIsSelected] = useState(false);

  // Utilisez useEffect pour mettre à jour l'état local lorsque props.item.purchase change
  useEffect(() => {
    if (props.item ) {
      setIsSelected(props.item.purchase);
    }
  }, [props.item.purchase]);

  const toggleSelection = () => {
    if (props.item) {
      const updatedPurchase = !isSelected;
      setIsSelected(updatedPurchase);
      props.item.purchase = updatedPurchase;

      if (updatedPurchase) {
        // Ajoutez l'article sélectionné à purchasedProduct
        purchasedProduct.push(props.item);
      } else {
        // Retirez l'article de purchasedProduct s'il existe
        const index = purchasedProduct.findIndex((item) => item === props.item);
        if (index !== -1) {
          purchasedProduct.splice(index, 1);
        }
      }

      console.log("Produits sélectionnés : ", purchasedProduct);
    }
  };

  return (
    <TouchableOpacity
      style={styles.button}
      onPress={() => {
        if (!props.disabled) {
          props.navigation.navigate(props.screen);
        } else {
          toggleSelection();
        }
      }}
    >
      <Image style={styles.image} source={props.image}></Image>
      <Text style={styles.text}>{props.text} </Text>
      {isSelected && <Text>OK</Text>}
      {props.price && <Text>{props.price}€</Text>}
    </TouchableOpacity>
  );
}


const styles = StyleSheet.create({
  button: {
    flex: 1,
    alignItems: "center",
    backgroundColor: "rgba(45, 45, 45, 0.3)",
    flexDirection: "row",
    height: 100,
    padding: "3%",
    borderWidth: 1,
    margin: "1%",
  },
  text: {
    color: "white",
    fontSize: 15,
    marginLeft: 10,
  },
  image: {
    width: 60,
    height: 60,
  },
});
