import React, { useState, useEffect } from "react";
import { View, Text, ImageBackground } from "react-native";
import styles from "../../services/styles";
import Button from "../../components/Button";

import { purchasedProduct } from "../Products";

const poissons = [
  { title: "Filet Bar de ligne ", price: 7, purchase: false },
  { title: "Bar de ligne portion ", price: 10, purchase: false },
  { title: "Aile de raie ", price: 10, purchase: false },
  { title: "Lieu jaune de ligne ", price: 12, purchase: false },
  { title: "Filet Julienne ", price: 19, purchase: false },
  { title: "Bar de ligne ", price: 30, purchase: false },
];

const logo = require("../../assets/images/logo/poulpe.png");
const disabled = true;

const ListPoissons = () => {
  const [selectedItems, setSelectedItems] = useState([]);

  // Mettre à jour les éléments sélectionnés lorsque purchasedProduct change
  useEffect(() => {
    setSelectedItems(purchasedProduct.map((item) => item.title));
  }, [purchasedProduct]);

  return (
    <View style={styles.container}>
      <ImageBackground
        source={require("../../assets/images/background.png")}
        style={styles.imagebackground}
      >
        {poissons.map((poisson, index) => (
          <Button
            item={poisson}
            key={index}
            disabled={disabled}
            text={poisson.title}
            image={logo}
            price={poisson.price}
            isSelected={selectedItems.includes(poisson.title)}
          />
        ))}
      </ImageBackground>
    </View>
  );
};

export default ListPoissons;
