import React from 'react';
import { View, Text, Button, ImageBackground } from 'react-native';
import { StackScreenProps } from '@react-navigation/stack';
import HeaderProducts from '../components/HeaderProducts';
import styles from '../services/styles';
import { purchasedProduct } from "./Products";


type RootStackParamList = {
  Main: undefined;
  Detail: undefined;
  // Ajoutez d'autres écrans ici
};

type Props = StackScreenProps<RootStackParamList, 'Main'>;
const disabled = true;
const logo = require("../../assets/images/logo/poulpe.png");
class Panier extends React.Component<Props> {
  render() {
    return (
      /*<View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
        <Text>Panier Screen</Text>
        <Button
          title="Go to Details"
          onPress={() => this.props.navigation.navigate('Main')}
        />
      </View>*/

    <View style={styles.container}>
        <ImageBackground source={require("../assets/images/background.png")} style={styles.imagebackground}>
            <HeaderProducts navigation={this.props.navigation}></HeaderProducts>
            <View style={styles.header2}>
                <Text > Modifiez la quantité en tapant sur chaque produit</Text>
            </View>
            {purchasedProduct ? "" : purchasedProduct.map((product, index) => (
          <Button
            item={product}
            key={index}
            disabled={disabled}
            text={product.title}
            image={logo}
            price={product.price}
          />
        ))}

        </ImageBackground>
    </View>
    );
  }
}

export default Panier;
